INSERT INTO `product` (`id`, `name`, `category_id`, `code`, `price`, `availability`, `brand`, `image`, `description`, `is_new`, `is_recommended`, `status`, `size`) VALUES
(14, 'TPA Western Flavor', 8, 1, 30, 1, 'TPA', '', 'Богатый табачный аромат. Рекомендуемая концентрация составляет 2-5%. Для раскрытия всех оттенков вкуса, готовую жидкость рекомендуется настоять, как минимум, 2-3дня', 0, 1, 1, 5),
(15, 'Acetyl Pyrazine 5 PG', 8, 2, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Acetyl Pyrazine 5 PG- добавка, которая успешно используется во всех типах жидкостей - в табачных она смягчает и подслащивает вкус, в выпечке дает "зернистую" структуру, шоколадные вкусы она очень усиливает. Крайне рекомендуем экспериментировать с этой добавкой, так как она может преобразить ваш рецепт в лучшую сторону.', 0, 1, 1, 5),
(16, 'Acai Flavor', 8, 3, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Acai Berry со вкусом ягоды асаи отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми.', 0, 0, 1, 5),
(17, 'Apple (Tart Green Apple) Flavor', 8, 4, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Green Apple со вкусом Пирога из кислого зеленого яблока отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми.', 0, 1, 1, 5),
(18, 'Apple Flavor', 8, 5, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Apple со вкусом сладкого красного яблока отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми.', 0, 1, 1, 5),
(20, 'TPA Apple Pie Flavor', 8, 6, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Apple Pie с насыщенным вкусом яблочного пирога отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками.', 0, 1, 1, 5),
(22, 'TPA Apricot Flavor', 8, 7, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Apricot со вкусом абрикоса отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми.', 0, 0, 1, 5),
(23, 'TPA Banana Cream Flavor', 8, 8, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Banana Cream со вкусом сладкого бананового крема отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно с кремовыми', 0, 0, 1, 5),
(24, 'TPA Banana Flavor', 8, 9, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Banana со вкусом банана отлично покажет себя как в соло замесе, так и в миксах с другими аромками, особенно фруктовыми и выпечкой', 0, 1, 1, 5),
(25, 'Banana Nut Bread Flavor', 8, 10, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Banana Nut Bread со вкусом бананового кекса отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми и кремовыми.', 0, 1, 1, 5),
(26, 'Bananas Foster Flavor', 8, 11, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Bananas Foster со вкусом бананового фостера - десерта из бананов и ванильного мороженого с соусом из сливочного масла, коричневого сахара, корицы, тёмного рома и бананового ликёра', 0, 1, 1, 5),
(27, 'TPA Bavarian Cream Flavor', 8, 12, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Bananas Foster со вкусом бананового фостера - десерта из бананов и ванильного мороженого с соусом из сливочного масла, коричневого сахара, корицы, тёмного рома и бананового ликёра', 0, 1, 1, 5),
(28, 'TPA Berry Mix Flavor', 8, 13, 30, 1, 'TPA', '', 'Микс ягодных вкусов', 0, 1, 1, 5),
(30, 'TPA Black Cherry Flavor', 8, 14, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Black Cherry со вкусом спелой вишни отличается насыщенностью и максимальной близостью к натуральной сладкой черной вишне. Отлично показывает себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми.', 0, 1, 1, 5),
(31, 'TPA Black Honey Flavor', 8, 15, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Black Honey Tobacco - сладковатый классический трубочный табак со множеством оттенков. Отлично подходит в соло замесе, сообенно для любителей Captain Black, так и в замесах с другими табачными аромками, мятой и кремами\r\n\r\nДанный ароматизатор сейчас называется Black Honey - раньше он назывался Black Honey Tobacco, и именно так он известен на сайтах с рецептами', 0, 1, 1, 5),
(32, 'TPA Blackberry Flavor', 8, 16, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Blackberry со вкусом ежевики отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми.', 0, 1, 1, 5),
(33, 'TPA Blueberry (Wild) Flavor', 8, 17, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Blueberry (Wild) со вкусом дикой черники отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми. Отличие от Blueberry (Extra) - Более свежий вкус, ароматизаторы абсолютно разные, стоит попробовать оба.', 0, 1, 1, 5),
(34, 'TPA Blueberry (Extra) Flavor', 8, 18, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Blueberry (Extra) со вкусом спелой черники отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно с кремовыми, получится подобие черничного пирога', 0, 1, 1, 5),
(35, 'TPA Bourbon Flavor', 8, 19, 30, 1, 'TPA', '', 'Ароматизатор со вкусом Бурбона!', 0, 1, 1, 5),
(36, 'TPA Brown Sugar Flavor', 8, 20, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Brown Sugar со вкусом тростникового сахара чаще всего используется как подсластитель, отлично показывает себя в кремовых замесах. Вкус очень концентрированный, рекомендуется добавлять немного.', 0, 1, 1, 5),
(37, 'TPA Bubblegum Flavor', 8, 21, 30, 1, 'TPA', '', 'Вкус мятной жвачки.', 0, 1, 1, 5),
(38, 'TPA Butter Flavor', 8, 22, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Butter со вкусом масла придаст должную "кремовость" и структуру Вашему замесу. Отлично сочетается с кастардами, кремами, и выпечкой', 0, 1, 1, 5),
(39, 'TPA Butterscotch Flavor', 8, 23, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Butterscotch со вкусом ирисок отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками', 0, 1, 1, 5),
(40, 'TPA Cantaloupe Flavor', 8, 24, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Cantaloupe со вкусом дыни канталупы отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми.', 0, 1, 1, 5),
(41, 'TPA Cappuccino Flavor', 8, 25, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Cappuccino со вкусом каппучино отлично сочетается с шоколадом', 0, 1, 1, 5),
(42, 'TPA Caramel (Original) Flavor', 8, 26, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Caramel (Original) со вкусом карамели отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно кремовыми. Обладает насыщенным, "кленовым" вкусом', 0, 1, 1, 5),
(43, 'TPA Caramel Candy Flavor', 8, 27, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Caramel Candy со вкусом карамельного леденца отлично подойдет в любые фруктовые и выпечные замесы.', 0, 1, 1, 5),
(44, 'TPA Cheesecake (Graham Crust)', 8, 28, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Cheesecake (Graham Crust) со вкусом чизкейка отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно с фруктовыми, можно получить различные вариации чизкейков, начиная от классического "Нью-Йорк", заканчивая фруктовыми с начинкой', 0, 1, 1, 5),
(45, 'TPA Cheesecake Flavor', 8, 29, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Cheesecake со вкусом чизкейка отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно с фруктовыми, можно получить различные вариации чизкейков, начиная от классического "Нью-Йорк", заканчивая фруктовыми с начинкой', 0, 1, 1, 5),
(46, 'TPA Cherry Blossom (PG)', 8, 30, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Cherry blossom (PG) со вкусом цветов вишни. Легкий и элегантный вкус, одним он напоминает вишню, другим - легкий и освежающий вкус', 0, 1, 1, 5),
(47, 'TPA Cinnamon Danish Flavor', 8, 31, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Cinnamon Danish со вкусом булочки с корицей. Отлично может дополнить любой замес не терпким вкусом корицы с выпечкой. При этом можно использовать как основу для жидкости - к этому вкусу необходимо только добавить что-нибудь кремове и фруктовое - получится настоящий cinnaBon', 0, 1, 1, 5),
(48, 'TPA Cinnamon Red Hot (PG)', 8, 32, 30, 1, 'TPA', '', 'Cinnamon Red Hot от The Perfumer’s Apprentice- Красная корица. Это «урезанная» версия корицы, которая содержит в основном коричный альдегид и эвгенол.', 0, 1, 1, 5),
(49, 'TPA Cinnamon Spice Flavor', 8, 33, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Cinnamon Spice со вкусом корицы. Данная аромка позволит придать любому Вашему замесу пикантность.', 0, 1, 1, 5),
(50, 'TPA Cinnamon Sugar Cookie', 8, 34, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Cinnamon Sugar Cookie со вкусом сахарного печенья с корицей отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно с кремовыми', 0, 1, 1, 5),
(51, 'TPA Coconut Flavor', 8, 35, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Coconut со вкусом кокоса не совсем подходит как самостоятельный вкус, а вот в сочетании с фруктовыми и тропическими ароматизаторами придает замечательный оттенок и послевкусие', 0, 1, 1, 5),
(52, 'TPA Coffee Flavor', 8, 36, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Coffee со вкусом свежего кофе, содержит натуральную эссенцию кофе, придающую аромке насыщенный цвет.', 0, 1, 1, 5),
(53, 'TPA Double Chocolate (Clear)', 8, 37, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Double Chocolate (Clear) со вкусом шоколада отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми и ягодными. Вкус является насыщенным и глубоким.', 0, 1, 1, 5),
(54, 'TPA Dragonfruit Flavor', 8, 38, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Dragonfruit со вкусом тропического фрукта питахайи (фрукт дракона) обладает ярким, свежим сладковатым вкусом и  отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно с фруктовыми и кремовыми', 0, 1, 1, 5),
(55, 'TPA DX Hazelnut Flavor', 8, 39, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice DX Hazelnut со вкусом лесного ореха (фундука) отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно шоколадными и кремовыми.', 0, 1, 1, 5),
(56, 'TPA French Vanilla Flavor', 8, 40, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice French Vanilla со вкусом сливочного ванильного десерта, очень похожим на мороженое, покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктово-кремовыми и фруктовыми.', 0, 1, 1, 5),
(57, 'TPA Fruit Circles Flavor', 8, 41, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Fruit Circles со вкусом фруктовых колечек из быстрого завтрака обладает фруктовым "cereal" вкусом с оттенком свежего лимона. Отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно кремовыми и фруктовыми.', 0, 1, 1, 5),
(58, 'TPA Hazelnut Flavor', 8, 42, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Hazelnut со вкусом лесного ореха (фундука) отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно шоколадными и кремовыми.', 0, 1, 1, 5),
(59, 'TPA Honeydew Flavor', 8, 43, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Honeydew со вкусом дыни является более доступным аналогом известного ароматизатора Honeydew, при этом не является огнеопасным. Сочный вкус сладкой спелой дыни отлично подойдет для любых фруктовых замесов.', 0, 1, 1, 5),
(60, 'TPA Irish Cream II Flavor', 8, 44, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Irish Cream со вкусом ирландского крема является, по сути, вкусом любимого многими ликера Бейлис. Отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно кремовыми.', 0, 1, 1, 5),
(61, 'TPA Koolada 10 PG', 8, 45, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Koolada 10 PG является 10% раствором в пропиленгликоле некоей Koolada. Обладает "охлаждающим" эффектом без привкуса мяты, отлично подойдет если Вы хотите сделать свежую жидкость, не изменяя ее вкус.', 0, 1, 1, 5),
(62, 'TPA Lemon Lime Flavor', 8, 46, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Lemon lime.', 0, 1, 1, 5),
(63, 'TPA Mango Flavor', 8, 47, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Mango со вкусом Манго отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми. Манго считается лучшим насыщенным вкусом.', 0, 1, 1, 5),
(64, 'TPA Orange Mandarin', 8, 48, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Orange Mandarin со вкусом спелого мандарина отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми.', 0, 1, 1, 5),
(65, 'TPA Papaya Flavor', 8, 49, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Papaya со вкусом тропического фрукта папайя. Отлично подойдет любителям фруктовых миксов.', 0, 1, 1, 5),
(66, 'TPA Peppermint Flavor', 8, 50, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Peppermint со вкусом перечной мяты отлично покажет себя в композиции с другими аромками, особенно фруктовыми. С этой аромой Вы можете сделать любую жидкость освежающей.', 0, 1, 1, 5),
(67, 'TPA Raspberry (Sweet) Flavor', 8, 51, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Raspberry (Sweet) со вкусом сладкой малины отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми и кремовыми.', 0, 1, 1, 5),
(68, 'TPA Strawberry (Ripe) Flavor', 8, 52, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Strawberry (Ripe) со вкусом спелой клубники отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми и кремовыми. Отличается от оригинального ароматизатора Strawberry более сладким и свежим вкусом.', 0, 1, 1, 5),
(69, 'TPA Sweet Cream Flavor', 8, 53, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Sweet Cream со вкусом сладкого крема отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми и кремовыми.', 0, 1, 1, 5),
(70, 'TPA Vanilla Bean Gelato Flavor', 8, 54, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Vanilla Bean Gelato. Джелато - это популярный итальянский замороженный десерт, отличающийся от мороженого низким содержанием молочных жиров и большим количеством сахара. Данная аромка открывает другую сторону вкуса замороженого десерта, отличаясь от Vanilla Bean Ice Cream. Никто не мешает Вам использовать их одновременно, чтобы сделать комплексный вкус ванильного мороженого.', 0, 1, 1, 5),
(71, 'TPA Vanilla Bean Ice Cream', 8, 55, 30, 1, 'TPA', '', 'Ароматизатор The Perfumer''s Apprentice Vanilla Bean Ice Cream со вкусом ванильного мороженого отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми (клубнично-ванильное мороженое - феерия вкуса).', 0, 1, 1, 5),
(72, 'TPA Vanilla Custard Flavor', 8, 56, 30, 1, 'TPA ', '', 'Ароматизатор The Perfumer''s Apprentice Vanilla Custard со вкусом ванильного крема отлично покажет себя и как самостоятельный вкус, и в композиции с другими аромками, особенно фруктовыми. Ванильный кастард(крем) - классический ароматизатор, использующийся многими производителями премиальных жидкостей в сладких, десертных жидкостях.', 0, 1, 1, 5),
